INSERT INTO `get_reptile_id` VALUES (1, '');
INSERT INTO `hero_base` VALUES (7, '凯隐', '影流之镰', 'http://img5.dwstatic.com/lol/1706/362766482649/1498811286269.jpg', '孩童已死，杀手永存。”悉达·凯隐是修行暗影魔法的精英，他战斗的意义，是为了实现自己真正的命运——为了有朝一日能够率领影流教派，开创艾欧尼亚霸业的新世代。', 'http://lol.duowan.com/kayn/');
INSERT INTO `hero_base` VALUES (8, '洛', '幻翎', 'http://img4.dwstatic.com/lol/1704/357246207960/1493291013439.jpg', '对洛特兰部落而言，瓦斯塔亚的洛是臭名远播的捣蛋鬼，同时也是有史以来最为出色的战舞舞者。', 'http://lol.duowan.com/rakan/');
INSERT INTO `hero_base` VALUES (9, '霞', '逆羽', 'http://img1.dwstatic.com/lol/1704/357253735391/1493298541562.jpg', '身为瓦斯塔亚的志士，霞要掀起一场革命来拯救他的族群', 'http://lol.duowan.com/xayah/');
INSERT INTO `hero_base` VALUES (10, '卡蜜尔', '青钢影', 'http://img2.dwstatic.com/lol/1611/344428862745/1480473717181.png', '卡蜜尔是一位优雅的特工精英，也是一台精密的人形兵器。', 'http://lol.duowan.com/camille/');
INSERT INTO `hero_base` VALUES (11, '艾翁', '翠植之祖', 'http://img1.dwstatic.com/lol/1609/338393194134/1474437997425.png', '凡是他走过的地方，无不是野花盛开、草木葱茏，回应着他的笑声。坚硬的岩石和他谈笑，鲜嫩的青苔与他一起品味阳光。', 'http://lol.duowan.com/ivern/');
INSERT INTO `hero_base` VALUES (12, '克烈', '暴怒骑士', 'http://img2.dwstatic.com/lol/1607/333467056501/1469511859510.png', '他是一个疯狂前进的战士，在战场上肆意冲锋是他的使命。对他而言，在战场上，疯狂比勇气更可靠。', 'http://lol.duowan.com/kled/');
INSERT INTO `hero_base` VALUES (13, '塔莉垭', '岩雀', 'http://img5.dwstatic.com/lol/1605/326373421236/1462418224471.jpg', '在战场中，她是一位擅长控场的法师，可以将脚下的土地编织成致命的武器。一旦她决定了自己的去向，大地便会为她架桥开路，保驾护航。', 'http://lol.duowan.com/taliyah/');
INSERT INTO `hero_base` VALUES (14, '索尔', '铸星龙王', 'http://s1.dwstatic.com/vhuya/46/1C/461ca5a38486824d6e5736161628db5d7655.png', '自从降生于太息之初，他便在无垠的空无中遨游，寻觅着散落于广袤深空中可称奇伟的光华，渲染他亘古凭飞的漫卷长天。正如星空之龙来自天外，奥瑞利安•索尔从未遇见过任何能与之比肩的存在。', 'http://lol.duowan.com/sol/');
INSERT INTO `hero_base` VALUES (15, '烬', '戏命师', 'http://img4.dwstatic.com/lol/1602/320083658274/1456128461876.png', '作为一名心思缜密的癫狂杀手，烬坚信谋杀是一门艺术。他曾在艾欧尼亚的监狱中服刑，但却因为执政议会里涌动着的暗流而得到释放，成为了权术斗争所利用的刺客。', 'http://lol.duowan.com/jhin/');
INSERT INTO `hero_base` VALUES (16, '俄洛伊', '海兽祭司', 'http://img.dwstatic.com/lol/1511/311181024943/1447226977586.png', '俄洛伊的体格强横无比，却唯独会在她的坚定信仰面前屈身。作为大海兽的先知，她挥舞着一个巨大的金色神像，将敌人的灵魂抽离体外，将他们对现实的感知完全击毁。', 'http://lol.duowan.com/illaoi/');
INSERT INTO `hero_base` VALUES (17, '千珏', '永猎双子', 'http://img5.dwstatic.com/lol/1509/306349973746/1442394784384.jpg', '千珏作为一对在丛林中成长的射手，千珏徘徊于野怪的营地之间，伺机为敌人打上预言死亡的印记。当千珏将谶语付诸现实时，他们的力量将会得到永久地增长。', 'http://lol.duowan.com/Kindred/');
INSERT INTO `hero_base` VALUES (18, '塔姆', '河流之主', 'http://img5.dwstatic.com/lol/1506/299071575541/1435116378901.jpg', '塔姆肯奇是一个既可以出现在单人路也可以担任坦克辅助的结实角色，什么东西他都能吃下。当敌人在他面前时，叠满被动后他会心满意足的将其吞噬到自己的大肚子里。', 'http://lol.duowan.com/tahmkench/');
INSERT INTO `hero_base` VALUES (19, '艾克', '时间刺客', 'http://img1.dwstatic.com/lol/1505/295539535718/1431584341356.jpg', 'Ekko足智多谋，多年来混迹于祖安街头。他能一眼看穿敌人的弱点，攻破敌人的防线。他善于从过去的经历中吸取教训，是个完美主义者;为了能充分利用每分每秒，不惜牺牲自己的肉体。', 'http://lol.duowan.com/ekko/');
INSERT INTO `hero_base` VALUES (20, '巴德', '星界游神', 'http://img2.dwstatic.com/lol/1503/289477079770/1425521886799.jpg', '巴德游历于各个超乎凡间生命体想象的秘界之间，当不可知的宇宙构造受到威胁时，巴德就会引导所有存在的事物避过彻底灭绝的命运。', 'http://lol.duowan.com/bard/');
INSERT INTO `hero_base` VALUES (21, '雷克赛', '虚空遁地者', 'http://img5.dwstatic.com/lol/1412/281530572893/1417575385364.jpg', '雷克赛是她族群之中体型最大、脾气最暴的。一旦雷克赛侦测到你，你的命运就已注定。毫无逃生的希望，她是来自沙砾之下的死亡。', 'http://lol.duowan.com/reksai/');
INSERT INTO `hero_base` VALUES (22, '卡莉丝塔', '复仇之矛', 'http://img5.dwstatic.com/lol/1411/279641746944/1415686553224.jpg', '卡莉丝塔是一个不灭的报复灵体，对所有告密者、欺诈者和背叛者都怀着一股寒彻人心的憎恨之情。', 'http://lol.duowan.com/kalista/');
INSERT INTO `hero_base` VALUES (23, '阿兹尔', '沙漠皇帝', 'http://img.dwstatic.com/lol/1409/273519298897/1409564119766.jpg', '曾是一名被自大和傲慢所蒙蔽的凡人，对灼热沙漠有着无上统治权的阿兹尔以一个飞升者的身份重新归来。', 'http://lol.duowan.com/azir/');
INSERT INTO `hero_base` VALUES (24, '纳尔', '迷失之牙', 'http://img.dwstatic.com/lol/1408/273091424599/1409136231951.png', '丛林不会原谅盲目与无知。每根断枝都在讲述着故事。——纳尔', 'http://lol.duowan.com/gnar/');
INSERT INTO `hero_base` VALUES (25, '布隆', '弗雷尔卓德之心', 'http://img5.dwstatic.com/lol/1405/264077743227/1400123196470.png', '在弗雷尔卓德的冰冷山峰，布隆的故事开始了。他的传奇壮举激励着这里的所有人。他已准备好带着他的盾牌再上沙场，他知道他身上有着弗雷尔卓德人民的崇敬与希望。\"。', 'http://lol.duowan.com/braum/');
INSERT INTO `hero_base` VALUES (26, '维克兹', '虚空之眼', 'http://img1.dwstatic.com/lol/1402/256326391926/1392371196098.jpg', '“我来到这突然的强光中。眨，眨，眨，眨。我的眼睛开始适应并评估眼前的景色。”-维克兹', 'http://lol.duowan.com/velkoz/');
INSERT INTO `hero_base` VALUES (27, '亚索', '疾风剑豪', 'http://img4.dwstatic.com/lol/1311/249491473153/1385536285032.png', '“我的逃亡之旅到底还需要持续多久？即使是最强之风，终有一天也会在瓦罗兰大陆中逝去”-亚索', 'http://lol.duowan.com/yasuo/');
INSERT INTO `hero_base` VALUES (28, '金克丝', '暴走萝莉', 'http://img5.dwstatic.com/lol/1310/245353650426/1381398467886.png', '金克丝活着就是为了不计后果的四处肆虐，在她身后留下的只有一片混乱与恐慌。她是一名暴躁而残忍的罪犯，她蔑视一切仅仅是因为无聊，她会在她认为最无趣的地方：皮尔特沃夫。', 'http://lol.duowan.com/jinx/');
INSERT INTO `hero_base` VALUES (29, '卢锡安', '圣枪游侠', 'http://img2.dwstatic.com/lol/1308/240574668479/1376619482925.png', '卢锡安使用的是拥有传承力量的神圣武器，并成为对付不死族群的卫士。 他冷静而强大，面对无尽的恐怖时仍然可以惩罚对手通过其拥有的驱除之火。', 'http://lol.duowan.com/lucian/');
INSERT INTO `hero_base` VALUES (30, '亚托克斯', '暗裔剑魔', 'http://img2.dwstatic.com/lol/1306/235048838354/1371093644361.png', '“战者，或为荣耀，或为赞颂。然此皆仅为战所存也。”——亚托克斯 ', 'http://lol.duowan.com/aatrox/');
INSERT INTO `hero_base` VALUES (31, '丽桑卓', '冰霜女巫', 'http://img2.dwstatic.com/lol/1305/231871544650/231871561051.png', '闭上眼睛，让寒冷将你夺去。”——丽桑卓', 'http://lol.duowan.com/lissandra/');
INSERT INTO `hero_base` VALUES (32, '扎克', '生化魔人', 'http://img5.dwstatic.com/lol/1303/228242985967/228242993255.png', '“即使你没有脊柱，你依然必须站起来。”—扎克', 'http://lol.duowan.com/zac/');
INSERT INTO `hero_base` VALUES (33, '奎因', '德玛西亚之翼', 'http://img3.dwstatic.com/lol/1302/225381420352/225382419902.png', '大多数士兵会依赖他们的武器。只有少数人才会真正地相互依赖。——奎因', 'http://lol.duowan.com/quinn/');
INSERT INTO `hero_base` VALUES (34, '锤石', '魂锁典狱长', 'http://img3.dwstatic.com/lol/1301/222714364061/222714396868.png', ' 魂锁典狱长的过去鲜为人知，并且大多数细节只存在于童谣和传闻中。它们讲述着一个丧心病狂的狱卒的故事。', 'http://lol.duowan.com/thresh/');
INSERT INTO `hero_base` VALUES (35, '蔚', '皮城执法官', 'http://pic2.duowan.com/lol/1212/219251307713/219251317038.png', '“真遗憾。我有两只拳头，但你的脸上只放得下一只。”-蔚', 'http://lol.duowan.com/vi/');
INSERT INTO `hero_base` VALUES (36, '娜美', '唤潮鲛姬', 'http://pic.duowan.com/lol/1211/218041518261/218041566099.jpg', '“我即是狂澜，且无法被力挽。”-唤潮鲛姬·娜美。', 'http://lol.duowan.com/nami/');
INSERT INTO `hero_base` VALUES (37, '劫', '影流之主', 'http://pic4.duowan.com/lol/1211/216813960863/216813987373.jpg', '“均衡只是一个骗局，我们才是真正的忍者！”影流之主-劫', 'http://lol.duowan.com/zed/');
INSERT INTO `hero_base` VALUES (38, '伊莉丝', '蜘蛛女皇', 'http://pic5.duowan.com/lol/1210/215627370104/215627419666.png', '“真正的追随者无需害怕蜘蛛的拥抱。”-伊莉丝', 'http://lol.duowan.com/elise/');
INSERT INTO `hero_base` VALUES (39, '卡兹克', '虚空掠夺者', 'http://pic3.duowan.com/lol/1209/212665448739/212665677660.jpg', '如果你愿意探索大自然的奇妙，你会发现有相当一部分数量的动物充分适应着生存环境。当然，偶尔你会遇见一种生物能够适应任何环境。比如：虚空掠夺者-卡\'兹克。', 'http://lol.duowan.com/khazix/');
INSERT INTO `hero_base` VALUES (40, '辛德拉', '暗黑元首', 'http://pic3.duowan.com/lol/1208/210247342385/210247356941.png', '”能量属于那些能够运用它的人！\"-辛德拉', 'http://lol.duowan.com/syndra/');
INSERT INTO `hero_base` VALUES (41, '雷恩加尔', '傲之追猎者', 'http://img3.dwstatic.com/lol/1411/279647578275/1415693006769.png', '“单手宰羊，不如双手屠狼。”——雷恩加尔。', 'http://lol.duowan.com/rengar/');
INSERT INTO `hero_base` VALUES (42, '黛安娜', '皎月女神', 'http://pic.duowan.com/lol/1208/207845244716/207847215830.jpg', '黛安娜投身于战斗的热浪中，挥舞着她月牙枪刃，无情地施放着她的月光能量。', 'http://lol.duowan.com/diana/');
INSERT INTO `hero_base` VALUES (43, '婕拉', '荆棘之兴', 'http://pic.duowan.com/lol/1207/207224467449/207225320108.png', '靠近那些美丽的花儿，就靠近一些荆棘”-婕拉', 'http://lol.duowan.com/zyra/');
INSERT INTO `hero_base` VALUES (44, '杰斯', '未来守护者', 'http://pic.duowan.com/lol/1207/205773506081/205773517075.png', '　　武装着睿智与魅力，以及标志性的可变形铁锤，杰斯毕生致力于守护他的祖国，皮尔特沃夫。', 'http://lol.duowan.com/jayce/');
INSERT INTO `hero_base` VALUES (45, '德莱文', '荣耀行刑官', ' http://pic2.duowan.com/lol/1206/202484172754/202487208149.png', '　　德莱文的所有行刑都变成了一个考验，诺克萨斯的囚犯们为了最后的一线生机而奔跑的考验。他把这个考验当做他的个人舞台，并且将行刑转变成了一种主流的娱乐方式。', 'http://lol.duowan.com/draven/');
INSERT INTO `hero_base` VALUES (46, '德莱厄斯', '诺克萨斯之手', 'http://img5.dwstatic.com/lol/1605/327431848757/1463477081676.png', '团结一致的诺克萨斯能够掌控整个世界！而且理应如此！', 'http://lol.duowan.com/darius/');
INSERT INTO `hero_base` VALUES (47, '赫卡里姆', '战争之影', 'http://img1.dwstatic.com/lol/1407/269890592132/1405936914678.png', '这位高大魁梧、披坚执锐的幽魂就已将恐惧深深地植入到了那些见过他的人们的心中。', 'http://lol.duowan.com/hecarim/');
INSERT INTO `hero_base` VALUES (48, '韦鲁斯', '惩戒之箭', 'http://pic3.duowan.com/lol/1204/198079462173/198079477246.jpg', '为了镇压一个远古的腐败深渊而建的一间寺院。这个深渊是那么地令人不安。韦鲁斯被一间神圣的艾欧尼亚寺院选作守望者。', 'http://lol.duowan.com/varus/');
INSERT INTO `hero_base` VALUES (49, '璐璐', '仙灵女巫', 'http://pic4.duowan.com/lol/1203/196696818878/196703018435.png', '璐璐率性而为的程度，也许联盟中没有哪个英雄能够比得上。在班德尔城中度过的青春岁月中，她总是把大部分时间都花在林间漫步或者做白日梦上。', 'http://lol.duowan.com/lulu/');
INSERT INTO `hero_base` VALUES (50, '菲奥娜', '无双剑姬', 'http://img.dwstatic.com/lol/1508/304523821745/1440568627197.png', '\'想和我共舞一曲死亡的华尔兹么？\'-菲奥娜', 'http://lol.duowan.com/fiora/');
INSERT INTO `hero_base` VALUES (51, '诺提勒斯', '深海泰坦', 'http://pic4.duowan.com/lol/1202/193328146015/193333009937.gif', '诺提勒斯苏醒时，他变得有些不太一样了。庞大的铁质潜水服已经变成了一个浑然一体的甲壳。', 'http://lol.duowan.com/nautilus/');
INSERT INTO `hero_base` VALUES (52, '瑟庄妮', '凛冬之怒', 'http://img2.dwstatic.com/lol/1407/269890592132/1405937100681.png', '　当瑟庄妮出生的时候，部落的预言者就说过，她将会成为统一这个分裂部族的领袖。', 'http://lol.duowan.com/sejuani/');
INSERT INTO `hero_base` VALUES (53, '吉格斯', '爆破鬼才', 'http://pic4.duowan.com/lol/1202/192131965665/192131975792.gif', '吉格斯生来就有着捣鼓机械的天赋，但他自由散漫、过度亢奋的天性在约德尔科学家当中实属罕见。很早以前，吉格斯就已经制造出了一批被他亲昵地称为\"海克斯爆破\"的强力炸弹。', 'http://lol.duowan.com/ziggs/');
INSERT INTO `hero_base` VALUES (54, '维克托', '机械先驱', 'http://img3.dwstatic.com/lol/1410/278074218098/1414119027670.png', '在年轻时，维克托就发现了他对科学与发明的激情，特别是机械自动化这一领域。他参加了祖安享有盛名的科技魔法大学，并带领了一支队伍，研发出了布里兹这一科学史上的突破。', 'http://lol.duowan.com/viktor/');
INSERT INTO `hero_base` VALUES (55, '阿狸', '九尾妖狐', 'http://img5.dwstatic.com/lol/1605/327431848757/1463477114973.png', '即便阿狸已成人形，她也知道这次幻化并不完整。作为一只机敏的生物，她很快就适应了人类社会的风俗礼仪，并以她不可多得的天生丽质去吸引心无戒备的男人。', 'http://lol.duowan.com/ahri/');
INSERT INTO `hero_base` VALUES (56, '沃利贝尔', '雷霆咆哮', 'http://pic.duowan.com/lol/1111/186599851229/186599920512.jpg', '沃利贝尔开始了他漫长而危险的登山历程。当他到达了顶峰，凝视着上面怒吼的风暴，他看见了一个可怕的景象——弗雷卓德整个被战争笼罩。', 'http://lol.duowan.com/volibear/');
INSERT INTO `hero_base` VALUES (57, '菲兹', '潮汐海灵', ' http://pic.duowan.com/lol/1111/185550440004/185552341067.gif', '菲兹在大海中流浪数年去寻找他的人民，用他在冒险中学来的本领生存。最终，菲兹到达了比尔吉沃特港口。', 'http://lol.duowan.com/fizz/');
INSERT INTO `hero_base` VALUES (58, '特朗德尔', '巨魔之王', 'http://img1.dwstatic.com/lol/1306/235591678174/1371636483394.png', '一个睿智的萨满发现他可以将整个部落的诅咒通过魔法转移到某一个巨魔战士身上，然而部落里一直没有这样的战士出现...直至特朗德尔出生。', 'http://lol.duowan.com/trundle/');
INSERT INTO `hero_base` VALUES (59, '拉克丝', '光辉女郎', 'http://img3.dwstatic.com/lol/1407/269890592132/1405935685198.png', '拉克丝天生就属于显赫的皇冠卫队，出自德玛西亚的模范家庭她注定要成就一番伟业。', 'http://lol.duowan.com/lux/');
INSERT INTO `hero_base` VALUES (60, '布兰德', '复仇焰魂', 'http://img.dwstatic.com/lol/1407/269890592132/1405937171546.png', '古书里将其称为复仇焰魂。这个一个充满灼热仇恨的生物，它的存在就是为了将人类和约德尔人生存的地方夷为平地。', 'http://lol.duowan.com/brand/');
INSERT INTO `hero_base` VALUES (61, '希瓦娜', '龙血武姬', ' http://img.dwstatic.com/lol/1409/275653745481/1411698550859.png', '以父之名，赐你死亡！', 'http://lol.duowan.com/shyvana/');
INSERT INTO `hero_base` VALUES (62, '格雷福斯', '法外狂徒', 'http://img5.dwstatic.com/lol/1411/279647578275/1415692717334.png', '格雷福斯出生在比尔吉沃特酒馆的后院，和一瓶混了酒精的牛奶一起被遗弃。残酷的现实迫使他在众多城市的地下社会勉强度日。', 'http://lol.duowan.com/graves/');
INSERT INTO `hero_base` VALUES (63, '泽拉斯', '远古巫灵', 'http://pic1.duowan.com/lol/1110/183638755440/183638769835.jpg', '摆脱了血肉之躯后，泽拉斯几乎能掌控无尽的能量。', 'http://lol.duowan.com/xerath/');
INSERT INTO `hero_base` VALUES (64, '图奇', '瘟疫之源', 'http://img.dwstatic.com/lol/1410/276710213760/1412756788167.jpg', '他在佐恩——也就是赋予他进化的感知的城市，找到了一位慷慨的资助者。图奇是佐恩在联盟的最早的英雄之一，当瓦洛兰城邦起了政治纷争之时，他便为佐恩而战。。', 'http://lol.duowan.com/twitch/');
INSERT INTO `hero_base` VALUES (65, '锐雯', '放逐之刃', ' http://img4.dwstatic.com/lol/1310/246296550427/1382341365723.png', '她是残忍高效的战士，而力量源自内心坚定的信念。', 'http://lol.duowan.com/riven/');
INSERT INTO `hero_base` VALUES (66, '泰隆', '刀锋之影', 'http://img2.dwstatic.com/lol/1411/279649187628/1415694426798.png', '\"你只能在ducouteau家族看到瓦罗然大陆上最致命的三名用刀高手：我的父亲，我，泰隆。\" ——卡特琳娜，不详之刃', 'http://lol.duowan.com/talon/');
INSERT INTO `hero_base` VALUES (67, '斯卡纳', '水晶先锋', 'http://pic.duowan.com/lol/1108/178647387266/178647406694.jpg', '\"人类尚未学会控制他们的魔法——因为卡拉曼达已经变成了历史长河中的一条水晶疮疤（意指新地图水晶之痕）。\" ', 'http://lol.duowan.com/skarner/');
INSERT INTO `hero_base` VALUES (68, '孙悟空', '齐天大圣', 'http://img2.dwstatic.com/lol/1411/279649187628/1415694802272.png', '\"通往全知的第一步是发现自己的无知。\"——易大师的第一课 ', 'http://lol.duowan.com/wukong/');
INSERT INTO `hero_base` VALUES (69, '蕾欧娜', '曙光女神', 'http://pic2.duowan.com/lol/1205/200657192402/200657210799.png', '太阳的光芒笼罩着符文大陆，而其化身也势必如此。 - 蕾欧娜', 'http://lol.duowan.com/leona/');
INSERT INTO `hero_base` VALUES (70, '约里克 ', '掘墓者', 'http://pic3.duowan.com/lol/1104/166448533784/175792432633.jpg', '于生者而言，掘墓人的工作不可或缺，而在暗影岛，掘墓人的价值更不可估量', 'http://lol.duowan.com/yorick/');
INSERT INTO `hero_base` VALUES (71, '瑞兹', '流浪法师', 'http://img2.dwstatic.com/lol/1407/269890592132/1405937405275.png', '作为法师学院出来的两个讨厌鬼之一，瑞兹从来没合群过,在他7岁时，瑞兹就曾半夜偷入导师的住所，然后用一瓶电能药剂烧焦了校长引以为傲的...', 'http://lol.duowan.com/ryze/');
INSERT INTO `hero_base` VALUES (72, '卡特琳娜', '不祥之刃', 'http://img4.dwstatic.com/lol/1605/327431848757/1463476917030.png', '在军国主义的诺克萨斯，女人的首要责任就是养育强壮的小孩，为参军的丈夫传宗接代，这是意料之中的事。但是有些女人却不把养育当作自己的天性，卡特琳娜便是其中之一...', 'http://lol.duowan.com/katarina/');
INSERT INTO `hero_base` VALUES (73, '裟娜', '琴瑟仙女', ' http://img4.dwstatic.com/lol/1310/246295394773/1382341114754.png', '当娑娜尚在襁褓之时，她便被抛弃在艾欧尼亚的一家孤儿院的门前。因而她对自己的亲生父母毫无记忆...', 'http://lol.duowan.com/sona/');
INSERT INTO `hero_base` VALUES (74, '斯维因', '策士统领', 'http://img2.dwstatic.com/lol/1605/327431848757/1463476767064.png', '有关斯维因的最早记载出现在诺克萨斯养老院一名医生的笔记上。据载，斯维因一瘸一拐地走进病房，没有叫喊也没有抱怨，他的右腿被折成两段，骨头破皮而出。他的肩膀上站着一只阴森的小鸟，仿佛是黏在他肩上似的...', 'http://lol.duowan.com/swain/');
INSERT INTO `hero_base` VALUES (75, '乐芙兰', '诡术妖姬', ' http://img5.dwstatic.com/lol/1305/231871544650/231873618947.png', '每个城市都有你所不知的一面，正如每个人也都有着不为他人所知的一面。诺克萨斯，就是这么一个让人又爱又恨的地方...', 'http://lol.duowan.com/leblanc/');
INSERT INTO `hero_base` VALUES (76, '艾瑞莉娅', '刀锋意志', ' http://pic5.duowan.com/lol/1205/200657192402/200657210786.png', '当绝望的艾欧尼亚人在普雷希典巨台准备投降时，艾瑞莉娅却高举起先父的传世之剑，誓死保卫家园直到兄长带领援兵归来...', 'http://lol.duowan.com/irelia/');
INSERT INTO `hero_base` VALUES (77, '卡西奥佩娅', '魔蛇之拥', ' http://img.dwstatic.com/lol/1409/275654100149/1411698909355.png', '当姐姐卡特琳娜成为家里最著名的一员时，杜-克卡奥家族已为诺克萨斯服务了很长时间。人们总说，杜-克卡奥将军比任何军人都幸运，因为他有两个女儿陪伴在旁。小女儿卡西奥佩娅虽然没有姐姐的超凡能力，却因其高贵和典雅的气质而同样为人熟知...', 'http://lol.duowan.com/cassiopeia/');
INSERT INTO `hero_base` VALUES (78, '凯特琳', '皮城女警', ' http://img3.dwstatic.com/lol/1407/269517769163/1405562576193.png', '皮特沃夫（皮城）被认为\"进步之城\"的主要原因是其极低的犯罪率。罗马不是一天建成的，皮特沃夫的低犯罪率也不是一来就有的。最初，皮特沃夫为了发展高科技，进口了很多珍贵资源，强盗和小偷们发现这块肥肉后便经常光顾这里。传说中的皮城女警凯特琳就此诞生...', 'http://lol.duowan.com/caitlyn/');
INSERT INTO `hero_base` VALUES (79, '雷克顿', '荒漠屠夫', ' http://img.dwstatic.com/lol/1409/275654100149/1411698914143.png', '雷克顿来自遥远一个的世界，和哥哥内瑟斯一样都是兽形种族的一份子，统治且保护着人类。雷克顿和内瑟斯一起管理秘藏着远古智慧和生死循环奥义的大图书馆...', 'http://lol.duowan.com/renekton/');
INSERT INTO `hero_base` VALUES (80, '卡尔玛', '天启者', 'http://img5.dwstatic.com/lol/1306/235155745723/1371200551407.png', '卡尔玛所的沉着冷静使她成为了村庄各种矛盾的调停者。她的导师总说：\"彼能起风，亦能止风...', 'http://lol.duowan.com/karma/');
INSERT INTO `hero_base` VALUES (81, '茂凯', '扭曲树精', ' http://pic.duowan.com/lol/1107/173890048848/173891429814.gif', '法师们在符文战争中施放扭曲魔法的能量给大自然造成了巨大的破坏。联盟的成立就是为了控制这种魔法的滥用，将其限制在正义之地。', 'http://lol.duowan.com/maokai/');
INSERT INTO `hero_base` VALUES (82, '嘉文四世', '德玛西亚皇子', 'http://img1.dwstatic.com/lol/1411/279647578275/1415692869825.png', '作为德玛西亚正统皇室光盾家族的成员，几个世纪以来他们都在与那些反对德玛西亚准则的敌人作战。据说每个光盾族人生来骨子里就有一股反诺克萨斯的情绪。嘉文四世也是如此。', 'http://lol.duowan.com/jarvan/');
INSERT INTO `hero_base` VALUES (83, '魔腾', '永恒梦魇', ' http://img1.dwstatic.com/lol/1410/276707403146/1412752216828.png', '在魔腾出现之前，人们都认为日有所思才会夜有所梦，梦只是他们在睡觉时昙花一现的无意义影像。但当联盟召唤师受到一连串和梦相关事件的影响后，人们开始怀疑这个说法。有些人尖叫从梦中惊醒，终日惶惶不安；有些人无法入睡，黑夜的到来让他们渐渐失去心智...', 'http://lol.duowan.com/nocturne/');
INSERT INTO `hero_base` VALUES (84, '李青', '盲僧', ' http://img.dwstatic.com/lol/1410/278090339703/1414135165242.jpg', '年轻的李青立志要成为一名召唤师，他的决心和奉献精神是同龄人无可比拟的。李青的才华引起了当时的联盟高级议员——瑞吉纳德-阿什拉姆的注意...', 'http://lol.duowan.com/leesin/');
INSERT INTO `hero_base` VALUES (85, '兰博', '机械公敌', 'http://img1.dwstatic.com/lol/1411/279647578275/1415693077770.png', '即便是在身材矮小的约德尔人中，兰波也只算矮个。所以他一直以来受人欺负。迫不得已，兰波变得比同辈更加好斗和机警。', 'http://lol.duowan.com/rumble/');
INSERT INTO `hero_base` VALUES (86, '薇恩', '暗夜猎手', ' http://img4.dwstatic.com/lol/1410/276707403146/1412752590005.png', '这个世界并不总像人们想象的那么美好。符文大陆上依旧有许多人通过最黑暗的方式修习魔法，被那些暗流涌动的黑暗力量腐蚀。夏娜-薇恩深知这一点。', 'http://lol.duowan.com/vayne/');
INSERT INTO `hero_base` VALUES (87, '奥莉安娜', '发条魔灵', 'http://img.dwstatic.com/lol/1410/277214494112/1413259306099.png', '住在在皮尔特沃夫corinreveck有个女儿名为奥莉安娜，科林爱她胜过世上一切。虽然奥莉安娜拥有惊人的舞蹈天赋，但她却为联盟的英雄着迷...', 'http://lol.duowan.com/orianna/');
INSERT INTO `hero_base` VALUES (88, '科加斯', '虚空恐惧', 'http://img.dwstatic.com/lol/1410/278090339703/1414135481125.jpg', '在各个维度各个世界之间，有这么一个地方。对有些人来说，这里是外域；对另一些人来说，则是未知空间；然而对更多的人而言，这里是the void...', 'http://lol.duowan.com/chogath/');
INSERT INTO `hero_base` VALUES (89, '努努', '雪人骑士', 'http://img1.dwstatic.com/lol/1310/246296550427/1382341365660.png', '瓦罗然极东之地被刺骨寒冰所覆盖，在漫长的冬季里，当地居民玩一个奇怪的游戏。他们弄醒一窝冬眠的雪人，然后挨个跳到雪人背上，比赛谁能骑乘...', 'http://lol.duowan.com/nunu/');
INSERT INTO `hero_base` VALUES (90, '阿木木', '殇之木乃伊', 'http://img1.dwstatic.com/lol/1407/269890592132/1405937554667.png', '阿木木是英雄联盟世界里最古老的保卫者英雄之一，但他对加入联盟前的生活仍一无所知。阿木木唯一记得的是自己在恕瑞玛沙漠的一座金字塔内醒来。', 'http://lol.duowan.com/amumu/');
INSERT INTO `hero_base` VALUES (91, '维迦', '邪恶小法师', 'http://img4.dwstatic.com/lol/1411/279649187628/1415694680759.png', '约德尔人在人们心中大多不能跟可怕挂钩。这个随和矮小的种族虽然有点狂野，但某种程度上还是容易亲近的。不过就在最近，有一个约德尔人变得非常坏……', 'http://lol.duowan.com/veigar/');
INSERT INTO `hero_base` VALUES (92, '辛吉德', '炼金术士', 'http://img4.dwstatic.com/lol/1411/279649187628/1415694348156.png', '在符文之战里有一个转折点，而我们只知道它被称为\"审判时\"。那天和平常一样，大陆上充满了愤怒的战斗，人们为不同的信仰战斗，然后死去。但在那...', 'http://lol.duowan.com/singed/');
INSERT INTO `hero_base` VALUES (93, '泰达米尔', '蛮族之王', 'http://img.dwstatic.com/lol/1407/269890592132/1405935684932.png', '这个世界上有那么一群选择不在瓦洛兰城邦生活的人，反之，他们选择区小村庄或者是游牧部落生活。在这些未经开拓的地方生活尽管存在一定的危险，但是同样也能享受到远离符文之地法规限制的自由...', 'http://lol.duowan.com/tryndamere/');
INSERT INTO `hero_base` VALUES (94, '蒙多', '祖安狂人', 'http://img.dwstatic.com/lol/1407/269890592132/1405937846531.png', '据说蒙多医生是出生在祖安的一个无情的男子，他有无法抑制的冲动去通过实验制造痛苦。在他五岁的时候，他周围邻居的宠物经常失踪；在他十岁的时候...', 'http://lol.duowan.com/mundo/');
INSERT INTO `hero_base` VALUES (95, '普朗克', '海洋之灾', 'http://img.dwstatic.com/lol/1507/300369495508/1436414308812.png', '普朗克的父亲是令人闻风丧胆的海盗船长\"魅影文森特\"，也是整个蓝焰岛上最富有、最恐怖的海盗之一。也许有人会认为普朗克会如温室的花朵一般被宠坏...', 'http://lol.duowan.com/gangplank/');
INSERT INTO `hero_base` VALUES (96, '墨菲特', '熔岩巨兽', 'http://img1.dwstatic.com/lol/1410/276707403146/1412752216963.png', '曾经有一个完全和谐的世界，在那里，一切都是这个整个世界的一部分。巨石是一切事物的本原，他们的外来居民则是一个个独立的部分。', 'http://lol.duowan.com/malphite/');
INSERT INTO `hero_base` VALUES (97, '希维尔', '战争女神', 'http://img1.dwstatic.com/lol/1407/269890592132/1405938229808.png', '美丽而致命的希维尔在她的雇佣兵生涯中赢得了无数头衔，而最近她一直是\" 诺克苏斯的战争女神\"。', 'http://lol.duowan.com/sivir/');
INSERT INTO `hero_base` VALUES (98, '费德提克', '末日使者', 'http://img1.dwstatic.com/lol/1605/327431848757/1463476955213.png', '20多年来费德提克独自站在战争学院最东边的召唤室。只有他双眼中发出的燃烧搬的绿色火焰才能刺穿他黑暗的、布满尘埃的家。末日使者就是在这里无声地守着。', 'http://lol.duowan.com/fiddlesticks/');
INSERT INTO `hero_base` VALUES (99, '凯尔', '审判天使', 'http://img4.dwstatic.com/lol/1411/279647578275/1415692934837.png', '许多年前，遥远的天国之中，崇尚自由的炎之天使长凯尔挑战了古板的最高指令.在神圣天国里，命令需要绝对服从，任何质疑都等于背叛。凯尔厌倦了毫无...', 'http://lol.duowan.com/kayle/');
INSERT INTO `hero_base` VALUES (100, '艾尼维亚', '冰晶凤凰', 'http://img3.dwstatic.com/lol/1605/327431848757/1463477136452.png', '大部分的传说里，凤凰都是从灰烬中重生的火鸟。但很少有人知道，凤凰是一种元素生物，由各自世界里永恒的精粹形成。艾尼维亚来自这样一个世界——...', 'http://lol.duowan.com/anivia/');
INSERT INTO `hero_base` VALUES (101, '提莫', '迅捷斥候', 'http://img2.dwstatic.com/lol/1310/246295394773/1382341114833.png', '作为班德尔城的斥候队长，提莫是祖国尤德尔最前方的防守者。在斥候队长之外，他还要筹划肥皂盒德比，传授新手斥候誓约，裁定年度大胃王比赛—...', 'http://lol.duowan.com/teemo/');
INSERT INTO `hero_base` VALUES (102, '艾希', '寒冰射手', 'http://img.dwstatic.com/lol/1305/231871544650/231872031019.png', '弗雷尔卓德美女艾希是英雄联盟召唤师长期以来的最爱之一。作为阿瓦罗萨的直系后代，艾希是传奇三姐妹之一。她们各自宣称统治着居住在瓦洛兰大陆北部冻苔原地带的散乱部落。艾希继承了祖先精通的箭术，因此也继承了阿瓦罗萨“寒冰射手”的称号。', 'http://lol.duowan.com/ashe/');
INSERT INTO `hero_base` VALUES (103, '安妮', '黑暗之女', 'http://img.dwstatic.com/lol/1310/246296550427/1382341365515.png', '她的母亲是一位灰色魔女，她的父亲是一名神秘的术士，这个孩子的血液里流淌着惊人的秘法能量。她两岁时就用魔法驯服了一头森林里的凶猛暗影熊，将...', 'http://lol.duowan.com/annie/');
INSERT INTO `hero_base` VALUES (104, '崔丝塔娜', '麦林炮手', 'http://img5.dwstatic.com/lol/1605/327431848757/1463476681702.png', '麦格林突击队一直是班德尔城里的一个传奇。他们拥有横跨数世纪的悠久历史，现在他们仍然还在约德尔人的军队里服役。他们以冷酷英勇而闻名于世，即使是班德尔城里面的勇士们也对他们敬畏有加...', 'http://lol.duowan.com/tristana/');
INSERT INTO `hero_base` VALUES (105, '贾克斯', '武器大师', 'http://img2.dwstatic.com/lol/1310/246295394773/1382341114670.png', '除了贾克斯，很少有英雄是通过加入联盟后的行动来衡量自己。贾克斯是目前战争学院最多产的联赛战士。在加入联盟之前，贾克斯只是个平凡的雇佣士兵...', 'http://lol.duowan.com/jax/');
INSERT INTO `hero_base` VALUES (106, '易', '无极剑圣', 'http://img1.dwstatic.com/lol/1310/246296550427/1382341588085.png', '无极武馆是个非常神秘的存在，除了本门弟子其他人根本找不到，传言武馆位于沙漠深处武馆有四位大师镇馆，分别以金银铜铅四条龙命名，他们从不在同时...', 'http://lol.duowan.com/yi/');
INSERT INTO `hero_base` VALUES (107, '库奇', '英勇投弹手', 'http://img2.dwstatic.com/lol/1310/246295394773/1382341114450.png', '库奇是\"尖啸之蛇\"里最具盛名的，不单因为他无与伦比的勇气，还有他面对威胁时冷静的头脑。在加入联盟之前，库奇也效力过其他组织，他经常主动接受任务，深入敌后，在危险地带收集情报或传递信息...', 'http://lol.duowan.com/corki/');
INSERT INTO `hero_base` VALUES (108, '阿利斯塔', '牛头酋长', 'http://img1.dwstatic.com/lol/1501/285778790953/1421825277654.jpg', '作为氏族的最后幸存者，阿利斯塔在保护家园抵抗强大入侵者时被俘，成为一名奴隶，被迫推动巨大的滑轮升降矿工。 在这无尽的痛苦生活里，他唯一的安...', 'http://lol.duowan.com/alistar/');
INSERT INTO `hero_base` VALUES (109, '布里茨', '蒸汽机器人', 'http://img4.dwstatic.com/lol/1411/279647578275/1415692397219.png', '祖安是个被科技和魔法双重扭曲的城市。没有节制的实验毁坏了这座城市。不过在魔法和科技的双重作用下，这里也出现了不少奇观，比如蒸汽机器人就是...', 'http://lol.duowan.com/blitzcrank/');
INSERT INTO `hero_base` VALUES (110, '索拉卡', '众星之子', 'http://img5.dwstatic.com/lol/1305/231871544650/231872754143.png', '在在符文之战中人中，索拉卡被称为众星之子。作为艾欧尼亚第一位高阶女祭司，她是艾欧尼亚的实际统治者，在符文之战中誓言保卫他们的信仰。', 'http://lol.duowan.com/soraka/');
INSERT INTO `hero_base` VALUES (111, '内瑟斯', '沙漠死神', 'http://img3.dwstatic.com/lol/1405/264160344403/1400205147348.png', '这个被称为内瑟斯的神秘生物从遥远的世界来到联盟，并且是庞大博学的沙漠帝国的一部分。在那里内瑟斯备受尊敬，同时学富五车的他被任命为大图书馆的守护者...', 'http://lol.duowan.com/nasus/');
INSERT INTO `hero_base` VALUES (112, '沃里克', '嗜血猎手', 'http://img.dwstatic.com/lol/1605/327431848757/1463477413939.png', '沃里克曾经是名野蛮的雇佣兵，他被祖安的炼金术士训练成为化学武器工程师。在爱奥尼亚一座城堡的防御战里，当己方部队都被消灭后，沃里克逃走了。...', 'http://lol.duowan.com/warwick/');
INSERT INTO `hero_base` VALUES (113, '莫甘娜', '堕落天使', 'http://img5.dwstatic.com/lol/1407/269888611908/1405934265741.png', '莫甘娜很小就随姐姐凯勒加入了审判者，姐妹俩迅速地脱颖而出，残酷的训练也将她们的战斗技能磨练到最高水准。虽然莫甘娜和凯勒是姐妹和亲密的朋友...', 'http://lol.duowan.com/morgana/');
INSERT INTO `hero_base` VALUES (114, '拉莫斯', '披甲龙龟', 'http://img2.dwstatic.com/lol/1605/327431848757/1463476821956.png', '瘟疫之地上蔓延的危险魔法影响了该地区的所有生灵，拉莫斯就是一个例子。没人知道来自shurima沙漠的龙龟怎么能毫发无损的穿越kumungu丛林，但拉...', 'http://lol.duowan.com/rammus/');
INSERT INTO `hero_base` VALUES (115, '卡萨丁', '虚空行者', 'http://img5.dwstatic.com/lol/1410/276710213760/1412756511788.png', '一个存在于各个世界之间的地方。对有些知道它存在的人来说是外域，而对于其他的人它则是一片未知之地。大部分人称其为做无尽虚空。不要被它的名...', 'http://lol.duowan.com/kassadin/');
INSERT INTO `hero_base` VALUES (116, '赛恩', '亡灵勇士', 'http://img3.dwstatic.com/lol/1410/277753050551/1413797864676.png', '德玛西亚和诺克萨斯的战争历史扩散到了瓦罗然的城市。这场冲突最新的产物是诺克苏斯的残暴战士赛恩。赛恩被诺克苏斯当作人形攻城武器使用，在...', 'http://lol.duowan.com/sion/');
INSERT INTO `hero_base` VALUES (117, '塔里克', '宝石骑士', 'http://pic2.duowan.com/lol/1205/200657192402/200657210836.png', '有一种古老的魔法，早已被人们所遗弃。这是有关大地之力的魔法，也是有关水晶和宝石共鸣的魔法。塔里克的父亲是一个遥远城镇中的医者，塔里克...', 'http://lol.duowan.com/taric/');
INSERT INTO `hero_base` VALUES (118, '迦娜', '风暴之怒', 'http://img5.dwstatic.com/lol/1411/279647578275/1415692794873.png', '有这么一些女巫，将自己奉献给自然的原始力量。有一个孤儿迦娜，成长于混乱的祖安，艰难的在街道上讨生活。祖安狂暴的魔法是她眼中最初也是最迷人的存在，迦娜意识到她可以用魔法来保护自己...', 'http://lol.duowan.com/janna/');
INSERT INTO `hero_base` VALUES (119, '基兰', '时光守护者', 'http://img1.dwstatic.com/lol/1411/279649187628/1415694905444.png', '从干枯破裂的河床上升起的是一座大摆钟一样的塔。塔没有门也没有其他入口，固若金汤，无懈可击，无法通过攀爬或飞行进入。塔外面方圆百里寸草...', 'http://lol.duowan.com/zilean/');
INSERT INTO `hero_base` VALUES (120, '崔斯特', '卡牌大师', 'http://img3.dwstatic.com/lol/1305/231871544650/231873225306.png', '蛇纹石河两岸是一个与世隔绝的吉普赛聚居地。任何一天你都会看见一队明轮船在河面急流中上下穿行。虽然宿营地一直在变动，但部族却非常稳定团...', 'http://lol.duowan.com/twisted/');
INSERT INTO `hero_base` VALUES (121, '伊芙琳', '寡妇制造者', 'http://img3.dwstatic.com/lol/1605/327431848757/1463477047695.png', '伊芙琳的来历至今仍是个谜，不过第一眼看上去她不太像个人类。有人猜测那是吸血鬼幼年时期的温和外表。也有人认为她在战场上爆发的野性暗示了。', 'http://lol.duowan.com/evelynn/');
INSERT INTO `hero_base` VALUES (122, '卡尔萨斯', '死亡颂唱者', 'http://img2.dwstatic.com/lol/1407/269606339574/1405651153711.png', '在符文之战期间，巨大的魔法能量被释放出来，给瓦罗然大陆留下了无数伤痕。很多偏远地区被残余的魔法持续扭曲，成为人迹罕至的地方，嚎叫沼泽...', 'http://lol.duowan.com/karthus/');
INSERT INTO `hero_base` VALUES (123, '萨科', '恶魔小丑', 'http://img3.dwstatic.com/lol/1411/279649187628/1415693994078.png', '从没有人会把死亡当作儿戏，除非你是萨科-这个瓦罗然大陆上第一个嗜杀的喜剧演员。他总是变着戏法折磨无辜的人，直到他们死去后他才放声大笑...', 'http://lol.duowan.com/shaco/');
INSERT INTO `hero_base` VALUES (124, '黑默丁格', '大发明家', 'http://img3.dwstatic.com/lol/1410/276707403146/1412757458195.png', '在班德勒郊外看到yordle一族是很不寻常的事情，但是科技的吸引对于黑默丁格来说无法抵挡的。科学院召唤他来效力，大胆的启用他.', 'http://lol.duowan.com/haimdinger/');
INSERT INTO `hero_base` VALUES (125, '阿卡丽', '暗影之拳', 'http://img2.dwstatic.com/lol/1410/277214494112/1413259306011.png', '艾欧尼亚群岛有一个古老的组织，他们将其一生奉献给世界平衡。秩序，混乱，光明，黑暗 —— 所有这一切都必须完美平衡的存在，这就是宇宙之道...', 'http://lol.duowan.com/akali/');
INSERT INTO `hero_base` VALUES (126, '乌迪尔', '兽灵行者', 'http://img1.dwstatic.com/lol/1411/279649187628/1415694612797.png', '在艾欧尼亚，有一群隐修者悉心钻研着瓦洛兰的至深奥秘，探索着灵魂的最深处。他们中的一些精英是文明与和谐的忠实拥护者，然而总有人另辟蹊径...', 'http://lol.duowan.com/udyr/');
INSERT INTO `hero_base` VALUES (127, '盖伦', '德玛西亚之力', 'http://img3.dwstatic.com/lol/1410/276707403146/1412752217106.png', '在瓦洛兰大陆，虽然人们对德玛西亚的军纪存在争议，但是相同点是所有人都尊敬它。平民和士兵都严格遵守着\"零容忍\"的准则。这意味着在战斗中徳玛西亚军队永远不会找借口托辞、逃跑、或者投降...', 'http://lol.duowan.com/garen/');
INSERT INTO `hero_base` VALUES (128, '凯南', '狂暴之心', 'http://img5.dwstatic.com/lol/1605/327431848757/1463476890582.png', '艾欧尼亚群岛有一个古老的组织，他们将其一生奉献给世界平衡。秩序，混乱，光明，黑暗 —— 所有这一切都必须完美平衡的存在，这就是宇宙之道...', 'http://lol.duowan.com/kennen/');
INSERT INTO `hero_base` VALUES (129, '克格莫', '深渊巨口', 'http://img.dwstatic.com/lol/1410/278090339703/1414135525151.jpg', '符文之地迷人的色彩和香气让克格\'莫沉醉，他用自己唯一知道的方法探寻这个奇怪世界里的水果，那就是吞食它们。', 'http://lol.duowan.com/kogmaw/');
INSERT INTO `hero_base` VALUES (130, '玛尔扎哈', '虚空先知', 'http://img1.dwstatic.com/lol/1605/327431848757/1463476853235.png', '大多数人在恕瑞玛令人刺眼的阳光下变得疯狂，但是当夜幕降临的时候玛尔扎哈也放下了自己的理智。自从他出生之时就受到了先知的祝福而拥有了未卜先知的能力。', 'http://lol.duowan.com/malzahar/');
INSERT INTO `hero_base` VALUES (131, '莫德凯撒', '金属大师', 'http://img.dwstatic.com/lol/1410/276710213760/1412755019210.jpg', '某天，从暗影岛来了一个器宇不凡的家伙。他身高八英尺，深深的隐藏在锋利的盔甲之内，人们只知道他名叫莫德凯撒。从未有人见过他藏在盔甲之中的真面目...', 'http://lol.duowan.com/mordekaiser/');
INSERT INTO `hero_base` VALUES (132, '奈德丽', '狂野女猎手', 'http://img.dwstatic.com/lol/1305/231871544650/231873517836.png', '超级壁垒的南部是一片被诅咒的凶险之地，因此人烟稀少，而英雄更是凤毛麟角。那里的大部分地区仍然残留着上次符文战争留下的创伤，特别是神秘的库莽古森林...', 'http://lol.duowan.com/nidalee/');
INSERT INTO `hero_base` VALUES (133, '伊泽瑞尔', '探险家', 'http://img2.dwstatic.com/lol/1411/279647578275/1415692576539.png', '伊泽瑞尔的血液中流淌着与生俱来的魔法天赋。与此同时，他热爱流浪探险。伊泽瑞尔进入学校后，有望成长为熟练的科技魔法师，但他很快就厌倦了学校的魔法学习...', 'http://lol.duowan.com/ezreal/');
INSERT INTO `hero_base` VALUES (134, '古拉加斯', '酒桶', 'http://img3.dwstatic.com/lol/1411/279647578275/1415692632471.png', '超级堡垒山麓孕育着瓦罗然大陆最坚毅的居民，只有不屈不挠并吃苦耐劳的人才能在符文之地最恶劣的环境下生存...', 'http://lol.duowan.com/gragas/');
INSERT INTO `hero_base` VALUES (135, '奥拉夫', '狂战士', 'http://img.dwstatic.com/lol/1407/269888611908/1405934460434.png', '在瓦罗然势力范围以外的未知大陆，奥拉夫出生于一个历史悠久、与众不同的勇士世家。他们自称为狂战士，将自己的怒火导入至战斗之中，释放出狂野的愤怒...', 'http://lol.duowan.com/olaf/');
INSERT INTO `hero_base` VALUES (136, '潘森', '战争之王', 'http://img1.dwstatic.com/lol/1407/269888611908/1405934598070.png', '在巨石峰巅的云端之上，居住着一个叫斯坦帕的部落，他们崇拜战争，视其为第二艺术。他们还记得符文之地发生的符文之战，也知道英雄联盟对部落崛起长久以来的压制...', 'http://lol.duowan.com/pantheon/');
INSERT INTO `hero_base` VALUES (137, '波比', '钢铁大使', 'http://lol.duowan.com/s/hero/bb.jpg', '当其他的约德尔年轻人在载歌载舞，互抛花环的时候，波比还在她父亲的铠甲店里辛勤的工作，双手长满了老茧，身上沾满了油渍，她的父亲...', 'http://lol.duowan.com/poppy/');
INSERT INTO `hero_base` VALUES (138, '慎', '暮光之眼', 'http://img.dwstatic.com/lol/1411/279649187628/1415694139441.png', '艾欧尼亚岛上有一个上古教派，致力于维护均衡。规则与混乱、光明与黑暗——万物必须和谐共存，这就是宇宙的真理。这个教派名为均衡教派，而该教派维护世界均衡的神圣使命则由三个暗影战士来执行...', 'http://lol.duowan.com/shen/');
INSERT INTO `hero_base` VALUES (139, '赵信', '德邦总管', 'http://pic5.duowan.com/lol/1205/200657192402/200657210853.png', '每当德玛西亚的国王嘉文三世在皇宫顶部那熠熠生辉的大理石阳台上发表他那激情澎湃的演讲之时，赵信都会侍立于他的身旁...', 'http://lol.duowan.com/xinzhao/');
INSERT INTO `hero_base` VALUES (140, '弗拉基米尔', '猩红收割者', 'http://img2.dwstatic.com/lol/1407/269888611908/1405934827815.png', '在诺克萨斯和暴风平原之间的群山中，坐落着一个寺院。这里守护着许多秘密。寺院里面堆了许多不小心闯入的旅行者尸体...', 'http://lol.duowan.com/vladimir/');
INSERT INTO `hero_base` VALUES (141, '加里奥', '哨兵之殇', 'http://img2.dwstatic.com/lol/1703/353430828133/1489478217150.png', '在很久之前，联盟并没有对法师创造人工生命进行任何监管，然而，现在已经彻底禁止了向傀儡灌输灵魂这样在工匠中并不罕见的活动。但是还有这样一位充满幻想德玛西亚工匠-杜兰德.', 'http://lol.duowan.com/galio/');
INSERT INTO `hero_base` VALUES (142, '厄加特', '首领之傲', 'http://img4.dwstatic.com/lol/1407/269517769163/1405562754544.png', '联盟里有很多的勇士，有些人是因为自己的天生神力而著名，有些充满了狡猾与诡计，或者是对于武器的专精，而其余的则只是简单的拒绝死亡。乌尔加特...', 'http://lol.duowan.com/urgot/');
INSERT INTO `hero_base` VALUES (143, '厄运小姐', '赏金猎人', 'http://img.dwstatic.com/lol/1407/269888611908/1405934761751.png', '对于勇敢面对符文之地严酷海洋的人，能拥有一艘属于自己的船和船员，就已是个不错的成就。备受尊敬的赏金猎人莎拉，在她16岁生日时就已经完成了这个目标...', 'http://lol.duowan.com/missfortune/');
INSERT INTO `hero_detail` VALUES (1239, '凯隐', '', '战士 打野 坦克', 80, 70, 10, 80, '影流之镰', 'http://img1.dwstatic.com/lol/1706/362765828385/1498810632997.jpg', '7800金币 ', ' 4500点卷', '', 'http://img3.dwstatic.com/lol/1711/374865254096/1510910140889.png', '主符文主宰系： \n基石天赋：电刑 \n类似于之前版本的雷霆领主的法令，属于增加爆发力以及强化换血能力的基石符文。电刑配合上凯隐的高爆发能够让他的爆发能力更加恐怖。 \n高级符文：恶意中伤 \n通过移动受损能够造成额外的真实伤害，在自身拥有控制技能的时候能够让你打出更高额的输出从而提升自己的爆发与换血能力。 \n基础符文：幽灵魄罗 \n当你进入草丛时，一个魄罗会出现。它会呆在后面来为你提供视野。通过这个符文能够给你提供不错的视野效果，通过进草丛来保证自己的视野安全。 \n基础符文：贪婪猎手 \n参与击杀之后能够增强你的技能伤害提供的治疗效果。对于频繁使用技能的英雄来说拥有了不错的续航能力并且还有一部分的回复量十分划算。 \n副符文精密系： \n基础符文：凯旋 \n凯旋在击杀英雄之后回复已损失生命值的15%，对于自身在残血击杀之后能够回复血量，团战中能够很好的保证自己的存活率，并且在1V1对拼里面往往也能够成为最后的赢家。凯旋能够很好的让R技能之后的凯隐获得更高的回复生命值。 \n基础符文：致命一击 \n对于低生命值的敌方英雄造成额外伤害，无论是输出英雄还是一个半肉战士，这个符文的性价比永远是最高的选择。');
INSERT INTO `hero_detail` VALUES (1240, '洛', NULL, '辅助', 60, 10, 60, 50, '幻翎', 'http://img1.dwstatic.com/lol/1704/357240787010/1493285606821.jpg', '6300金币 ', ' 4500点卷', '352992', 'http://img2.dwstatic.com/lol/1711/374609339437/1510656108569.jpg', '主符文坚决系（4点）： \n基石符文：守护者 \n守护者提供保护能力配合洛的Q技能和E技能可以给AD一个很不错的生存能力。 \n高级符文：生命源泉 \nW技能和R技能都可以触发，给AD一个不错的自身恢复能力。 \n基础符文：调节 \n提高自身坦度。 \n基础符文：调节 \n提高自身生命值上限。 \n副符文启迪系（2点）： \n基础符文：未来市场 \n更快的合成核心装备，提高洛的中前期作战能力。 \n基础符文：星界洞悉 \n装备和技能的减CD可以让洛更好的保护队友。');
INSERT INTO `hero_detail` VALUES (1241, '霞', NULL, '射手', 70, 80, 10, 50, '逆羽', 'http://img.dwstatic.com/lol/1704/357240787010/1493285606821.jpg', '7800金币 ', ' 4500点卷', '352994', 'http://img2.dwstatic.com/lol/1711/374868130307/1510913411715.png', '主符文精密： \n基石符文：强攻 \n三次普攻之后强化自己的伤害数值并且造成额外伤害，对于需要利用普攻频发触发的英雄来说，这个符文能够更强的提升你的爆发能力，并且还拥有不错的持续输出的伤害提升，可以说是最强的输出天赋没有之一。通过W技能的高攻速与高输出，强攻的后续伤害能够很容易打出全部输出。 \n高级符文：凯旋 \n凯旋在击杀英雄之后回复已损失生命值的15%，对于自身在残血击杀之后能够回复血量，团战中能够很好的保证自己的存活率，并且在1V1对拼里面往往也能够成为最后的赢家。 \n基础符文：欢欣 \n欢欣提供的永久攻速加成，通过打线上小兵或者是野怪就能够很轻松去得到额外的攻速加成，满层数之后相当于为你提供了一个短剑的18%攻速，在前期作战的性价比极高。 \n基础符文：致命一击 \n对于低生命值的敌方英雄造成额外伤害，无论是输出英雄还是一个半肉战士，这个符文的性价比永远是最高的选择。  \n副符文主宰： \n基础符文：血之滋味 \n通过血之滋味能够让你在对敌方英雄造成伤害的时候回血，无论是对线期还是残血的时候的缺少回复能力都能够通过血之滋味很好的处理。 \n基础符文：眼球收集器 \n在参与击杀英雄和守卫时收集眼球。每个眼球都会提供永久的攻击力或者法强，收集完成后能够提供额外的法强或攻击力。让你在杀人的同时能够增长自己的攻击属性更加强势。');
INSERT INTO `hero_detail` VALUES (1242, '卡蜜尔', NULL, '近战 战士', 90, 50, 10, 90, '青钢影', 'http://img2.dwstatic.com/lol/1612/345723005423/1481772330573.png', '6300金币 ', ' 4500点卷', '352083', 'http://img4.dwstatic.com/lol/1711/374688569360/1510736889273.jpg', '主符文精密（4点）： \n基石符文：强攻 \n强攻天赋配合卡蜜尔的Q技能独特机制，利用好自己的Q技能，AQAQ的连招能发挥出来强攻最大的特点，就是后续伤害拥有额外的提升。 \n基础符文：凯旋 \n击杀之后回复已损失生命值的15%在面对大部分英雄的时候，往往凯旋决定了自己的1V2能力究竟够不够强势。 \n基础符文：欢欣 \n拥有额外的攻速加成配合上提亚马特这件装备能够很好的让自己的带线能力体现出来，超强的单挑能力并且还能够更好的触发强攻效果。 \n基础符文：致命一击 \n在面对残血英雄能够拥有不错的额外伤害。 \n副符文精密系（2点）： \n基础符文：血之滋味 \n自己的普攻能够治疗自身，对于对拼的时候利用W技能收尾的青钢影能够保证自己在换血之后还拥有不错的回复能力。 \n基础符文：贪婪猎手 \n技能提供治疗效果，可以成功的让自己的技能吸血能力得到一个不小的提升。');
INSERT INTO `hero_detail` VALUES (1243, '艾翁', NULL, '辅助 法师 打野', 50, 30, 60, 80, '翠神', 'http://img1.dwstatic.com/lol/1610/340296771373/1476341582999.jpg', '6300金币 ', ' 4500点卷', '352074', 'http://img2.dwstatic.com/lol/1711/374609339437/1510656108569.jpg', '主符文坚决系（4点）： \n基石符文：守护者 \n守护者提供保护能力配合洛的Q技能和E技能可以给AD一个很不错的生存能力。 \n高级符文：生命源泉 \nW技能和R技能都可以触发，给AD一个不错的自身恢复能力。 \n基础符文：调节 \n提高自身坦度。 \n基础符文：调节 \n提高自身生命值上限。 \n副符文启迪系（2点）： \n基础符文：未来市场 \n更快的合成核心装备，提高洛的中前期作战能力。 \n基础符文：星界洞悉 \n装备和技能的减CD可以让洛更好的保护队友。');
INSERT INTO `hero_detail` VALUES (1244, '克烈', NULL, '近战 战士', 90, 50, 10, 90, '暴怒骑士', 'http://img3.dwstatic.com/lol/1608/335442871333/1471487684616.jpg', '6300金币 ', ' 4500点卷', '351927', 'http://img.dwstatic.com/lol/1711/374690374203/1510736750657.png', '主符文精密系： \n基石天赋：强攻 \n三次普攻之后强化自己的伤害数值并且造成额外伤害，克烈的W技能超高的攻速第四次强化普攻，利用前面三次可以拥有超强的爆发伤害。 \n高级符文：凯旋 \n凯旋在击杀英雄之后回复已损失生命值的15%，对于自身在残血击杀之后能够回复血量，团战中能够很好的保证自己的存活率，并且在1V1对拼里面往往也能够成为最后的赢家。 \n基础符文：欢欣 \n欢欣提供的永久攻速加成，通过打线上小兵或者是野怪就能够很轻松去得到额外的攻速加成，他能够让克烈在对拼中，更容易更快的打出来自己的强攻效果与自己的W技能伤害。搭配起来拥有十分恐怖的前期攻速。 \n基础符文：致命一击 \n对于低生命值的敌方英雄造成额外伤害，对于克烈这种持续作战能力与续航超强的英雄来说，致命一击就是真正能够置人于死地的精准符文。 \n副符文主宰系： \n基础符文：猛然冲击 \n通过位移技能或者是离开潜行状态能够获得双穿，通过克烈的E技能突进先手能够提升不少的伤害，8穿甲对于前期来说意义重大 \n基础符文：贪婪猎手 \n参与击杀之后能够增强你的技能伤害提供的治疗效果。而克烈的Q技能两段伤害与W技能的高爆发，都能够让贪婪猎手给你不错的回血效果。');
INSERT INTO `hero_detail` VALUES (1245, '塔莉垭', NULL, '远程 法师', 70, 10, 80, 50, '岩雀', 'http://img5.dwstatic.com/lol/1605/328023671635/1464068490712.jpg', '6300金币 ', ' 4500点卷', '352113', 'http://img3.dwstatic.com/lol/1711/374440699413/1510487102784.png', '主符文巫术系： \n基石天赋：相位猛冲 \n在岩雀用R技能封路后打出爆炸伤害，可以让岩雀的R既能变成功能性位移技能，也可以作为突进技能使用。 \n高级符文：法力流系带 \n岩雀的推线能力很强，但是蓝耗有事并不够，点出法力流系带可以提高岩雀的法力续航能力。 \n基础符文：迅捷 \n提高的移动速度可以增强岩雀的跑图能力，而移动速度变法强则是配合岩雀被动来提高伤害。 \n基础符文：焦灼 \n提高前期对线伤害能力。 \n副符文主宰系： \n基础符文：眼球收集器 \n岩雀的跑图能力给她创造了很多的GANK的机会，快速叠满眼球收集器可以让岩雀获取更多的属性。 \n基础符文：无情猎手 \n非战斗状态的移动速度加成可以提高岩雀的跑图能力。');
INSERT INTO `hero_detail` VALUES (1246, '奥瑞利安索尔', NULL, '法师', 20, 20, 80, 20, '铸星龙王', 'http://img3.dwstatic.com/lol/1603/322852568002/1458897488919.jpg', '7800金币 ', ' 4500点卷', '352114', 'http://img4.dwstatic.com/lol/1711/374252052042/1510301470441.png', '主符文巫术系（4点）： \n基石符文：相位俯冲 \n类似旧版的风暴骑手的狂涌，配合龙王的高爆发非常容易触发效果，追击必备。 \n高级符文：法力流系带 \n龙王的前期相对比较缺蓝，符文能有效缓解了龙王前中期部分的蓝耗问题。 \n基础符文：迅捷 \n龙王的优势就是在于前期的支援，增加移动速度对他来说非常有利，而额外的移动速度带来的法强加成，也非常契合三速鞋的属性。 \n基础符文：焦灼 \n龙王需要在前期占据推线优势和在线上尽可能压制对方，技能的灼烧效果能在换血上更加占据优势。 \n副符文主宰系（2点）： \n基础符文：恶意中伤 \nQ技能的晕眩和大招的减速效果，配合上符文能打出不错的COMBO。 \n基础符文：无情猎手 \n增加非战斗状态下的移动速度，让支援变得更加强力。');
INSERT INTO `hero_detail` VALUES (1247, '烬', NULL, '刺客 射手', 30, 90, 20, 50, '戏命师', 'http://img.dwstatic.com/lol/1602/320080222029/1456125025667.jpg', '6300金币 ', ' 4500点卷', '351960', 'http://img.dwstatic.com/lol/1711/374843973849/1510890935195.png', '主符文精密系： \n基石符文：强攻 \n三次普攻之后强化自己的伤害数值并且造成额外伤害，对于需要利用普攻频发触发的英雄来说，这个符文能够更强的提升你的爆发能力，并且还拥有不错的持续输出的伤害提升，可以说是最强的输出天赋没有之一。强攻配合上武器大师的超强持续输出能力能够让他的后续伤害更加充足。 \n基础符文：凯旋 \n凯旋在击杀英雄之后回复已损失生命值的15%，对于自身在残血击杀之后能够回复血量，团战中能够很好的保证自己的存活率，并且在1V1对拼里面往往也能够成为最后的赢家。凯旋对于大部分的战士英雄来说都十分适合。 \n基础符文：欢欣 \n欢欣提供的永久攻速加成，通过打线上小兵或者是野怪就能够很轻松去得到额外的攻速加成，满层数之后相当于为你提供了一个短剑的18%攻速，在前期作战的性价比极高。欢欣的成长能力能够让武器大师拥有不错的成长属性加成。 \n基础符文：致命一击 \n对于低生命值的敌方英雄造成额外伤害，无论是输出英雄还是一个半肉战士，这个符文的性价比永远是最高的选择。致命一击对于武器大师来说成为了残血击杀的关键。 \n副符文主宰系： \n基础符文：猛然冲击 \n通过位移技能或者是离开潜行状态能够获得双穿，对于刺客来说前期的双穿往往成为了想要击杀敌方英雄的最关键所在。猛然冲击配合武器的Q技能突进能够很好触发。 \n基础符文：贪婪猎手 \n参与击杀之后能够增强你的技能伤害提供的治疗效果。对于频繁使用技能的英雄来说拥有了不错的续航能力并且还有一部分的回复量十分划算。贪婪猎手能够让武器的技能输出提供回复，无论是W技能的频繁高额伤害还是R技能的被动都拥有很好的回复效果。');
