INSERT INTO `hero_equip` VALUES (476, '1041,2031,3340', '核心为九头蛇三项。乌迪尔短CD技能出三项很适合。', '3706,3074,3111,3078', NULL, '3706,3074,3111,3078,3046,3036', '脆是脆了点，切人非常疼。团战稍微注意切入点就好打。友情提示，尽量单挑不要打团。', '3706,3047,3071,3053,3512,3143', '传送门可以换成天使守护，蓝盾必出。', '352271');
